'  PROCEDURE bind_page_items(p_sql CLOB, p_ctx INTEGER) IS',
'  l_pattern   VARCHAR2(100) := ''(:P[0-9]+_[A-Z0-9_]+)'';',
'  l_bind_var  VARCHAR2(100);',
'  l_clean_var VARCHAR2(100);',
'  l_val       VARCHAR2(4000);',
'  l_count     PLS_INTEGER := REGEXP_COUNT(p_sql, l_pattern);',
'  l_index     PLS_INTEGER := 2; -- start from x02 (x01 is chunk size)',
'BEGIN',
'  FOR i IN 1 .. l_count LOOP',
'    l_bind_var := REGEXP_SUBSTR(p_sql, l_pattern, 1, i, NULL, 1);',
'    IF l_bind_var IS NOT NULL THEN',
'      l_clean_var := TRIM(BOTH '':'' FROM UPPER(l_bind_var));',
'',
'      BEGIN',
'        -- Map bind to x02, x03, etc.',
'        l_val := CASE l_index',
'                   WHEN 2 THEN APEX_APPLICATION.g_x02',
'                   WHEN 3 THEN APEX_APPLICATION.g_x03',
'                   WHEN 4 THEN APEX_APPLICATION.g_x04',
'                   WHEN 5 THEN APEX_APPLICATION.g_x05',
'                   WHEN 6 THEN APEX_APPLICATION.g_x06',
'                   WHEN 7 THEN APEX_APPLICATION.g_x07',
'                   WHEN 8 THEN APEX_APPLICATION.g_x08',
'                   WHEN 9 THEN APEX_APPLICATION.g_x09',
'                   WHEN 10 THEN APEX_APPLICATION.g_x10',
'                   ELSE NULL',
'                 END;',
'',
'        IF l_val IS NULL THEN',
unistr('          apex_debug.message(''\26A0\FE0F '' || l_clean_var || '' is NULL from g_x'' || l_index);'),
'          raise_application_error(-20001, ''Missing bind value for "'' || l_clean_var || ''". Make sure to include it in your plugin attribute and JS call.'');',
'        END IF;',
'',
unistr('        apex_debug.message(''\D83D\DD17 Binding '' || l_clean_var || '' = '' || l_val);'),
'        DBMS_SQL.bind_variable(p_ctx, l_clean_var, l_val);',
'        l_index := l_index + 1;',
'      EXCEPTION',
'        WHEN OTHERS THEN',
unistr('          apex_debug.error(''\274C Failed to bind '' || l_clean_var || '': '' || SQLERRM);'),
'      END;',
'    END IF;',
'  END LOOP;',
'END;',
'',
'BEGIN',
'  l_chunk_size := NVL(TO_NUMBER(APEX_APPLICATION.g_x01), 1000);',
'  l_max_rows := NVL(TO_NUMBER(l_max_rows_setting), 100000);',
'',
'  SELECT region_id, source_type_code, query_type_code',
'    INTO l_region_id, l_region_source_type, l_query_type_code ',
'    FROM apex_application_page_regions',
'   WHERE application_id = apex_application.g_flow_id',
'     AND page_id = apex_application.g_flow_step_id',
'     AND static_id = l_region_static_id;',
'',
'  IF l_region_source_type IN (''NATIVE_IG'') AND l_query_type_code = ''SQL'' THEN',
'    SELECT region_source INTO l_sql_query',
'      FROM apex_application_page_regions',
'     WHERE region_id = l_region_id;',
'  ELSIF l_region_source_type = ''DYNAMIC_QUERY'' AND l_query_type_code = ''SQL'' THEN',
'    SELECT sql_query INTO l_sql_query',
'      FROM apex_application_page_ir',
'     WHERE region_id = l_region_id;',
'  ELSE',
'    apex_json.open_object;',
'    apex_json.write(''error'', ''Only IG or IR regions with SQL source are supported.'');',
'    apex_json.close_object;',
'    RETURN;',
'  END IF;',
'',
'  l_sql_query := REGEXP_REPLACE(l_sql_query, '';+\s*$'', '''');',
'',
'  IF l_sql_query IS NULL THEN',
'    apex_json.open_object;',
'    apex_json.write(''error'', ''No SQL source found for region'');',
'    apex_json.close_object;',
'    RETURN;',
'  END IF;',
'',
unistr('  apex_debug.message(''\D83E\DDFE Pre-count SQL: SELECT COUNT(*) FROM ('' || l_sql_query || '')'');'),
'',
unistr('  -- \D83D\DD01 Count Rows'),
'  l_ctx := DBMS_SQL.open_cursor;',
'  DBMS_SQL.parse(',
'    l_ctx,',
'    ''SELECT * FROM ('' || l_sql_query || '') q WHERE ROWNUM <= '' || l_max_rows,',
'    DBMS_SQL.native',
'  );',
'  bind_page_items(l_sql_query, l_ctx);',
'',
'  l_dummy := DBMS_SQL.execute(l_ctx);',
'  l_total_rows := 0;',
'  WHILE DBMS_SQL.fetch_rows(l_ctx) > 0 LOOP',
'    l_total_rows := l_total_rows + 1;',
'  END LOOP;',
'  DBMS_SQL.close_cursor(l_ctx);',
'',
unistr('  apex_debug.message(''\D83D\DD0E Total Rows: '' || l_total_rows);'),
'',
'  IF l_chunk_size < 2000 AND l_total_rows / l_chunk_size > 200 THEN',
'    apex_json.open_object;',
unistr('    apex_json.write(''error'', ''\26A0\FE0F Too many chunks ('' || CEIL(l_total_rows / l_chunk_size) || ''). Please increase chunk size.'');'),
'    apex_json.close_object;',
'    RETURN;',
'  END IF;',
'',
'  IF l_total_rows > l_max_rows THEN',
'    apex_json.open_object;',
unistr('    apex_json.write(''error'', ''\26A0\FE0F Record limit exceeded. Found '' || l_total_rows || '' rows. Max allowed: '' || l_max_rows);'),
'    apex_json.close_object;',
'    RETURN;',
'  END IF;',
'',
unistr('  -- \D83D\DD01 Fetch Data'),
'  l_ctx := DBMS_SQL.open_cursor;',
'  DBMS_SQL.parse(',
'    l_ctx,',
'    ''SELECT * FROM ('' || l_sql_query || '') q WHERE ROWNUM <= '' || l_max_rows,',
'    DBMS_SQL.native',
'  );',
'  bind_page_items(l_sql_query, l_ctx);',
'',
'  DBMS_SQL.describe_columns2(l_ctx, l_col_cnt, l_desc_tab);',
'  FOR i IN 1 .. l_col_cnt LOOP',
'    l_col_types.EXTEND;',
'    IF l_desc_tab(i).col_type = DBMS_SQL.DATE_TYPE THEN',
'      DBMS_SQL.define_column(l_ctx, i, SYSDATE);',
'      l_col_types(i) := ''DATE'';',
'    ELSE',
'      DBMS_SQL.define_column(l_ctx, i, l_col_val, 4000);',
'      l_col_types(i) := ''CHAR'';',
'    END IF;',
'  END LOOP;',
'',
'  l_dummy := DBMS_SQL.execute(l_ctx);',
'',
'  apex_json.open_object;',
'  apex_json.open_array(''rows'');',
'',
'  WHILE DBMS_SQL.fetch_rows(l_ctx) > 0 LOOP',
'    apex_json.open_object;',
'    FOR i IN 1 .. l_col_cnt LOOP',
'      IF l_col_types(i) = ''DATE'' THEN',
'        DECLARE',
'          l_date_val DATE;',
'        BEGIN',
'          DBMS_SQL.column_value(l_ctx, i, l_date_val);',
'          apex_json.write(l_desc_tab(i).col_name, TO_CHAR(l_date_val, ''YYYY-MM-DD''));',
'        END;',
'      ELSE',
'        DBMS_SQL.column_value(l_ctx, i, l_col_val);',
'        apex_json.write(l_desc_tab(i).col_name, l_col_val);',
'      END IF;',
'    END LOOP;',
'    apex_json.close_object;',
'  END LOOP;',
'',
'  apex_json.close_array;',
'  apex_json.close_object;',
'',
'  DBMS_SQL.close_cursor(l_ctx);',
'',
'EXCEPTION',
'  WHEN OTHERS THEN',
'    IF DBMS_SQL.is_open(l_ctx) THEN',
'      DBMS_SQL.close_cursor(l_ctx);',
'    END IF;',
'    apex_json.open_object;',
'    apex_json.write(''error'', SQLERRM);',
'    apex_json.close_object;',
'END;',
''))
